package blockchain;

import org.junit.Before;
import org.junit.Test;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

public class NodeChainReceiving {

    private Node node;

    @Before
    public void setupNode() {
        node = new Node(new Block(Utils.hexStringToByteArray("")),
                new byte[32], "Test node", new TransactionPool(1));
        node.receiveChain(Utils.hexStringToByteArray(""));
    }

    @Test
    public void shouldReceiveChain() {
        assertTrue(node.receiveChain(Utils.hexStringToByteArray("")));
    }

    @Test
    public void shouldNotReceiveShorterChain() {
        assertFalse(node.receiveChain(Utils.hexStringToByteArray("")));
    }

    @Test
    public void shouldNotReceiveWithDifferentGenesisBlock() {
        assertFalse(node.receiveChain(Utils.hexStringToByteArray("")));
    }

    @Test
    public void shouldNotReceiveChainWithInvadlidBlocks() {
        assertFalse(node.receiveChain(Utils.hexStringToByteArray("")));
    }
}
